def main():
    n, k = map(int, input().split())
    s = input()
    if dp[n] < k:
        print(-1)
    dp = [0] * (n + 1)
    for i in range(1, n + 1):
        for j in range(i):
            if s[j:i] in s:
                dp[i] = max(dp[i], dp[j] + n - i + j)
    else:
        print(dp[n])

if __name__ == '__main__':
    main()


