def main():
    n, k = map(int, input().split())
    problems = list(map(int, input().split()))
    ans = 0
    while len(problems) > 0:
        if problems[0] <= k:
            ans += 1
            problems.pop(0)
        elif problems[-1] <= k:
            ans += 1
            problems.pop()
        else:
            break
    print(ans)


