def get_input():
    n, r = [int(x) for x in input().split()]
    a = [0] * n
    s = input()
    for i in range(n):
        if s[i] == 'o':
            a[i] = 1
    return n, r, a

def solve(n, r, a):
    ans = 0
    i = 1
    while i <= n:
        if a[i-1] == 0:
            j = i
            while j <= n and a[j-1] == 0:
                j += 1
            if j - i + 1 > 2*r:
                return -1
            if j <= n:
                a[j-1] = 1
                ans += 1
            else:
                return -1
            i = j
        else:
            i += 1
    return ans

def main():
    n, r, a = get_input()
    ans = solve(n, r, a)
    print(ans)

if __name__ == '__main__':
    main()

