X, K, D = map(int, input().split())

X = abs(X)
K = K - X // D
X = X % D
ans = X
if K % 2 == 1:
    ans = min(ans, abs(X - D))
print(ans)


import sys

def main():
    x, k, d = map(int, sys.stdin.readline().rstrip().split())
    if abs(x) < k * d:
        print(abs(x - (x // d) * d)) 
    else:
        print(abs(x) - (k * d))

if __name__ == '__main__':
    main()


# The point is to find the smallest possible absolute value of the
# coordinate of the destination.
# First, find the absolute value of (X - K * D). Then, find the remainder
# of (X - K * D) / (2 * D). If the remainder is 0, the distance is (X - K *
# D) / 2. If the remainder is 1, the distance is (X - K * D) / 2 + 1.

x, k, d = map(int, input().split())

if x < 0:
    x = -x

if x < k * d:
    k = k - (x + k * d) // (2 * d)
    x = (x + k * d) % (2 * d)

if x % (2 * d) == 0:
    print(x // (2 * d))
else:
    print(x // (2 * d) + 1)
