import sys
import math


def solve(n, m):
    if n == 1:
        return 0
    elif n == 2:
        return m
    else:
        return (m - n + 1) * 2


if __name__ == '__main__':
    t = int(sys.stdin.readline())
    for _ in range(t):
        n, m = map(int, sys.stdin.readline().split())
        print(solve(n, m))


def main():
    t = int(input())
    for _ in range(t):
        n, m = map(int, input().split())
        if n <= m:
            print(0)
        else:
            if n % m == 0:
                print(0)
            else:
                print(m - n % m)


if __name__ == '__main__':
    main()

