def main():
    n = int(input())
    h = list(map(int, input().split()))[::-1]
    ans = 0
    for i in range(n-1):
        if h[i] <= h[i+1]:
            ans += 1
        elif h[i] - 1 > h[i+1]:
            ans = -1
            break
    print(ans)

