# I was thinking about the following procedure.
# First, find the absolute value of X - K * D.
# Then, find the remainder of (X - K * D) / (2 * D). If the remainder is 0, the distance is (X - K * D) / 2. If the remainder is 1, the distance is (X - K * D) / 2 + 1.

X, K, D = map(int, input().split())

if X < 0: X = -X

if X < K * D:
    K = K - (X - K * D) // (2 * D)
    X = (X - K * D) % (2 * D)

if X % (2 * D) == 0:
    print(X // (2 * D))
else:
    print(X // (2 * D) + 1)
import sys

def main():
    x, k, d = map(int, sys.stdin.readline().rstrip().split())
    if abs(x) < k * d:
        print(abs(x - (x // d) * d))
    else:
        print(abs(x) - (k * d))

if __name__ == '__main__':
    main()


import sys

def main():
    n, a, b = map(int, sys.stdin.readline().rstrip().split())
    ans = 0
    if n < a + b:
        ans = 0
    elif a == b:
        ans = n - a
    else:
        ans = n
        if a < b:
            tmp = a
            a = b
            b = tmp
        ans -= a
        k = (a - b)
        ans -= k * ((ans - 1) // k)
    print(ans)


if __name__ == '__main__':
    main()


