import sys


def main():
    n = int(sys.stdin.readline())
    heights = list(map(int, sys.stdin.readline().split()))
    if n == 1:
        print('Yes')
        return
    prev_h = heights[0]
    for i in range(1, n):
        curr_h = heights[i]
        if curr_h < prev_h:
            if curr_h == 0:
                print('No')
                return
            heights[i] -= 1
        prev_h = heights[i]
    print('Yes')


if __name__ == '__main__':
    main()



def solve():
    n, m = map(int, input().split())
    a = [list(map(int, input().split())) for _ in range(n)]
    b = [list(map(int, input().split())) for _ in range(n)]
    for i in range(n):
        for j in range(m):
            a[i][j] = 1 if a[i][j] == b[i][j] else 0
    for i in range(n):
        for j in range(m):
            if a[i][j] == 0:
                for k in range(m):
                    a[i][k] = 1 - a[i][k]
                for k in range(n):
                    a[k][j] = 1 - a[k][j]
    for i in range(n):
        for j in range(m):
            if a[i][j] != b[i][j]:
                print('NO')
                return
    print('YES')



if __name__ == '__main__':
    solve()
