#define BUFFER_SIZE 1024

int main(int argc, char *argv[])
{
	int fd_src, fd_dest;
	ssize_t ret_in, ret_out;
	char buffer[BUFFER_SIZE];
	char *src_file = argv[1];
	char *dest_file = argv[2];

	if (argc != 3) {
		printf("Usage: %s <source_file> <dest_file>\n", argv[0]);
		exit(EXIT_FAILURE);
	}

	/* Open source file for reading */
	fd_src = open(src_file, O_RDONLY);
	if (fd_src < 0) {
		printf("Error: %s\n", strerror(errno));
		exit(EXIT_FAILURE);
	}

	/* Open destination file for writing */
	fd_dest = open(dest_file, O_WRONLY | O_CREAT, 0644);
	if (fd_dest < 0) {
		printf("Error: %s\n", strerror(errno));
		exit(EXIT_FAILURE);
	}

	/* Copy data from source file to destination file */
	while ((ret_in = read(fd_src, &buffer, BUFFER_SIZE)) > 0) {
		ret_out = write(fd_dest, &buffer, (ssize_t) ret_in);
		if (ret_out != ret_in) {
			printf("Error: %s\n", strerror(errno));
			exit(EXIT_FAILURE);
		}
	}

	/* Close files */
	close(fd_src);
	close(fd_dest);

	exit(EXIT_SUCCESS);
}
import os
import sys


def main():
    if len(sys.argv) < 2:
        print("Usage: {} <file>".format(sys.argv[0]))
        sys.exit(1)

    filename = sys.argv[1]

    if not os.path.exists(filename):
        print("{} does not exist".format(filename))
        sys.exit(1)

    if os.path.isfile(filename):
        print("{} is a file".format(filename))
    else:
        print("{} is not a file".format(filename))

    if os.path.isdir(filename):
        print("{} is a directory".format(filename))
    else:
        print("{} is not a directory".format(filename))

    if os.path.islink(filename):
        print("{} is a link".format(filename))
    else:
        print("{} is not a link".format(filename))

    if os.path.isabs(filename):
        print("{} is an absolute path".format(filename))
    else:
        print("{} is not an absolute path".format(filename))

    if os.path.isfile(filename):
        print("{} is a file".format(filename))

    if os.path.isfile(filename) and os.access(filename, os.R_OK):
        print("{} is readable".format(filename))

    if os.path.isfile(filename) and os.access(filename, os.W_OK):
        print("{} is writable".format(filename))

    if os.path.isfile(filename) and os.access(filename, os.X_OK):
        print("{} is executable".format(filename))


