def main():
    n = int(input())
    a = [int(x) for x in input().split()] # 入力をリストに格納
    a.append(0)
    a.insert(0, 0)
    s = [0] * (n+2) # 累積和
    for i in range(1, n+2):
        s[i] = s[i-1] + a[i]
    d = {}
    ans = 0
    for i in range(n+1):
        if s[i] not in d:
            d[s[i]] = i
        else:
            ans = max(ans, i - d[s[i]] - 1)
    print(ans)

if __name__ == "__main__":
    main()
