def solve(n, a, b, c):
    a, b = min(a, b), max(a, b)
    if a == b:
        return n * a
    if n % 2 == 0:
        return (a * (n//2)) + (b * (n//2))
    else:
        return (a * (n//2)) + (b * (n//2)) + a

