x, y = map(int, input().split())
if y % 2 == 0 and 4 * x <= y and y <= 2 * x:
    print("Yes")
else:
    print("No")

x, y = map(int, input().split())

if y % 2 == 0 and y / 2 >= x: # yが偶数で、xよりy/2が大きい場合
    print("Yes")
else:
    print("No")

A, B, C, X, Y = map(int, input().split())
ans = 10 ** 9
for i in range(10 ** 5 + 1):
    tmp = A * max(0, X - i) + B * max(0, Y - i) + 2 * C * i
    ans = min(ans, tmp)
print(ans)


