class Graph:
    def __init__(self, n):
        self.n = n
        self.adj = [[] for _ in range(n)]

    def add_edge(self, u, v):
        self.adj[u].append(v)

    def bfs(self, s):
        visited = [False] * self.n
        q = deque([s])
        while q:
            node = q.popleft()
            visited[node] = True
            for edge in self.adj[node]:
                if not visited[edge]:
                    q.append(edge)
        return visited


