N = int(input())

def is_shichigosan(n):
    if '7' in n and '5' in n and '3' in n:
        for i in range(1, 10):
            if str(i) in n and not i in [3, 5, 7]:
                return False
        return True
    else:
        return False

count = 0
for i in range(1, N+1):
    if is_shichigosan(str(i)):
        count += 1

print(count)

def sgsn(n):
    count = 0
    for i in range(1, n+1):
        if '7' in str(i) and '5' in str(i) and '3' in str(i):
            count += 1
    return count

n = int(input())
print(sgsn(n))
