def power_of_two(n):
    if n == 0:
        return False
    else:
        return n & (n-1) == 0


def solve(n, a):
    l = 0
    r = n-1
    res = n

    while l < r:
        if power_of_two(a[l]+a[r]):
            res -= 1
            l += 1
            r -= 1
        else:
            if a[l] > a[r]:
                r -= 1
            else:
                l += 1
    return res

print(solve(n, a))

