def dfs(v, p):
    for u in g[v]:
        if u != p:
            dfs(u, v)
            cnt[v] += cnt[u]


def dfs2(v, p):
    for u in g[v]:
        if u != p:
            cnt[u] += cnt[v]
            dfs2(u, v)


def dfs3(v, p):
    for u in g[v]:
        if u != p:
            dfs3(u, v)
            cnt[v] += cnt[u]


