import sys

class Solver:
    def __init__(self, n, a):
        self.n = n  # 数列の長さ
        self.a = a  # 数列

    def solve(self):
        def get_colors(s):
            colors = [1]
            for i in range(1, len(s)):
                if s[i] != s[i - 1]:
                    colors.append(colors[i - 1] + 1)
                else:
                    colors.append(colors[i - 1])
            return colors

        colors = get_colors(self.s)
        sorted_colors = get_colors(sorted(self.s))
        return [max(colors), " ".join(map(str, colors))]


