import sys


S = input().strip()

dict_ = {'R':'RUD','U':'URD','L':'LUD','D':'DRU'}

def takahashi_dance(s):
    for i in range(len(s)):
        if i%2 == 0:
            if s[i] not in dict_['L']:
                return 'No'
        elif s[i] not in dict_['R']:
            return 'No'
    return 'Yes'

print(takahashi_dance(S))

from collections import deque

def main():
    s = input()
    even = deque(s[::2])
    odd = deque(s[1::2])
    print("Yes" if all(list(map(lambda x: x in "RUD", odd))) and all(list(map(lambda x: x in "LUD", even))) else "No")

if __name__ == '__main__':
    main()


