import sys


def main(pname):
    sys.stdin = open(pname + '.in', 'r')
    sys.stdout = open(pname + '.out', 'w')

    n, k = map(int, input().split())
    arr = list(map(int, input().split()))
    cnt = [0] * (n + 1)
    for el in arr:
        cnt[el] += 1
    lim = min(k, max(cnt))
    if lim == 0:
        print('NO')
    else:
        print('YES')
        ans = [0] * n
        j = 1
        for i in range(n):
            if cnt[arr[i]] > 0:
                ans[i] = j
                cnt[arr[i]] -= 1
                if cnt[arr[i]] == 0:
                    j += 1
                    if j > lim:
                        j = 1
        print(*ans)


