                if l[left] > possible_total:
                    return 0
                elif l[left] == possible_total:
                    return 1
                elif sum(l[left:right]) == possible_total:
                    return 2
                elif sum(l[left:right]) < possible_total:
                    left += 1
                else:
                    right -= 1
            return 0
        else:
            return 0


