        print(2)
        return
    ans = 1
    ans1 = [1] * n
    ans2 = [1] * n
    for i in range(1, n):
        if a[i] > a[i-1]:
            ans1[i] = ans1[i-1] + 1
        else:
            ans1[i] = 1
        ans = max(ans, ans1[i])
