def dijkstra(graph, start, end):
    queue, enqueued = [(0, start, ())], set([start])
    while queue:
        (cost, v1, path) = heappop(queue)
        if v1 not in enqueued:
            enqueued.add(v1)
            path = (v1, path)
            if v1 == end:
                return (cost, path)

            for v2, c in graph[v1]:
                if v2 not in enqueued:
                    heappush(queue, (cost + c, v2, path))

    return float("inf")

