import sys
from collections import Counter

def get_input():
    input_lines = sys.stdin.readlines()
    num_strings = int(input_lines[0].strip())
    return input_lines[1:num_strings + 1]


def solve(s):
    s_counter = Counter(s)
    for key in s_counter:
        if s_counter[key] > 1:
            return "no"
    return "yes"


def print_result(results):
    for result in results:
        print(result)


def main():
    cases = get_input()
    results = []
    for string in cases:
        result = solve(string)
        results.append(result)
    print_result(results)


if __name__ == "__main__":
    main()
import sys

def get_input():
    input_lines = sys.stdin.readlines()
    num_cases = int(input_lines[0].strip()) #strip removes the newline character
    cases = []
    for i in range(num_cases):
        case = int(input_lines[i + 1].strip()) #strip removes the newline character
        cases.append(case)
    return cases


def solve(n):
    if n < 4:
        return 0
    if n % 2 == 1:
        return int(n / 2)
    return int(n / 2 - 1)


def print_result(results):
    for result in results:
        print(result)


def main():
    cases = get_input()
    results = []
    for case in cases:
        result = solve(case)
        results.append(result)
    print_result(results)


if __name__ == "__main__":
    main()
