def main():
    n = int(input())
    ans = ""
    if n == 0:
        ans = "0"

    else:
        while n != 0:
            if n % 2 == 0:
                ans += "0"
                n = n // -2
            else:
                ans += "1"
                n = (n - 1) // -2
        if ans[0] == "1":
            ans += "1"
        ans = ans[::1]

    print(ans)

if __name__ == "__main__":
    main()


def base_minus_2(num: int) -> str:
    if num == 0:
        return "0"

    res = ""
    while num != 0:
        if num % 2 == 0:
            res += "0"
        else:
            res += "1"
            num -= 1
        num //= -2

    return res[::-1]


if __name__ == "__main__":
    num = int(input())
    print(base_minus_2(num))

