import sys
input = lambda: sys.stdin.readline().rstrip()

n = int(input())

# -2の0乗〜n乗のリスト
pow_minus2 = [1]
for i in range(1, 32):
    pow_minus2.append(pow_minus2[i - 1] * -2)

# 累積和
cum_pow_minus2 = [0]
for i in range(1, 32):
    cum_pow_minus2.append(cum_pow_minus2[i - 1] + pow_minus2[i])

ans = ""
for i in range(31):
    if n == 0:
        break
    elif n > 0:
        if cum_pow_minus2[i] <= n < cum_pow_minus2[i + 1]:
            ans = "1" + ans
            n -= pow_minus2[i]
        else:
            ans = "0" + ans
    else:
        if cum_pow_minus2[i + 1] <= n < cum_pow_minus2[i]:
            ans = "1" + ans
            n -= pow_minus2[i]
        else:
            ans = "0" + ans

if ans == "":
    print(0)
else:
    print(ans)


def convert_base(num, base):
    if num < 0:
        num = -num
        sign = -1
    else:
        sign = 1

    res = ""
    while num > 0:
        rem = num % base
        num //= base
        if rem < 10:
            res += chr(ord('0') + rem)
        else:
            res += chr(ord('A') + rem - 10)

    if sign == -1:
        res += '-'

    return res[::-1] if res else "0"


if __name__ == "__main__":
    print(convert_base(int(input()), -2))

