import sys


input = sys.stdin.readline()

if len(input) == 1:
    sys.stdout.write(1)
else:
    count = 0
    for i in range(1, len(input)):
        if int(input[:i]) % 3 == 0:
            count += 1
    sys.stdout.write(count)


input = input('')

if len(input) == 1:
    print(1)
else:
    count = 0
    for i in range(1, len(input)):
        if int(input[:i]) % 3 == 0:
            count += 1
    print(count)


def sum_digits(n):
    return sum(map(int, str(n)))

def sum_digits_divisible_by_3(n):
    return sum(map(int, str(n))) % 3 == 0

def find_cuts(s):
    cuts = []
    for i in range(len(s) - 1):
        if not sum_digits_divisible_by_3(s[:i+1]):
            cuts.append(i + 1)
            continue
    if sum_digits_divisible_by_3(s[:i+1]) and i == len(s) - 1:
        return cuts
    else:
        return cuts + [len(s)]

def get_sub_strings(s, cuts):
    sub_strings = []
    i = 0
    for cut in cuts:
        sub_strings.append(s[i:cut])
        i = cut
    sub_strings.append(s[i:])
    return sub_strings

def main():
    s = input()
    cuts = find_cuts(s)
    sub_strings = get_sub_strings(s, cuts)
    count = 0
    for sub_string in sub_strings:
        if sum_digits_divisible_by_3(sub_string):
            count += 1
    print(count)

if __name__ == '__main__':
    main()


def is_divisible_by_three(n):
    return sum([int(x) for x in list(str(n))]) % 3 == 0

def max_divisible_three_cuts(s):
    count = 0
    for i in range(len(str(s))):
        for j in range(i + 1, len(str(s)) + 1):
            if is_divisible_by_three(int(str(s)[i:j])):
                count += 1
    return count

