x, y = map(int, input().split())
if y % 2 == 0 and 4 * x <= y and y <= 2 * x:
    print("Yes")
else:
    print("No")


def main():
    x, y = map(int, input().split())
    if x == (y // 2):
        print('Yes')
    elif x > (y // 2):
        print('No')
    elif x < (y // 2):
        print('No')

if __name__ == '__main__':
    main()


