def solve(n, m, k, A):
    # dp[i][j] = max sum of first i rows, with j columns chosen
    dp = [[0] * (m + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if j <= m // 2:
                dp[i][j] = max(dp[i][j - 1], dp[i - 1][j], dp[i - 1][j - 1] + A[i - 1][j - 1])
            else:
                dp[i][j] = dp[i][j - 1]

    return dp[n][m // 2] // k * k


