import sys
input = lambda: sys.stdin.readline().rstrip()

n, k = map(int, input().split())

# カウントを保存するリスト
cnt = [0] * (n + 1)

# 1からnまでの数字について、約数を求める
for i in range(1, n + 1):
    # 1からnまでの数字で割り切れる数をカウントする
    for j in range(1, n + 1):
        if i * j > n:
            break
        else:
            cnt[i * j] += 1

ans = 0
for i in range(1, n + 1):
    if cnt[i] == k:
        ans += 1

print(ans)

