import sys


def dfs(graph, v, parent, used):
    used[v] = True
    for u in graph[v]:
        if u == parent:
            continue
        if used[u]:
            continue
        if dfs(graph, u, v, used):
            return True
    return False


def solve(n, m, edges):
    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u - 1].append(v - 1)
        graph[v - 1].append(u - 1)
    used = [False] * n
    if dfs(graph, 0, -1, used):
        print('NO')
        return
    color = [None] * n
    for i in range(n):
        if color[i] is None:
            color[i] = 0
            stack = [i]
            while stack:
                v = stack.pop()
                for u in graph[v]:
                    if color[u] is None:
                        color[u] = 1 - color[v]
                        stack.append(u)
    for u, v in edges:
