    dp[0][a[0]] = 1
    for i in range(n - 1):
        for j in range(h):
            if dp[i][j] == 0:
                continue
            if (j + a[i + 1]) % h >= l and (j + a[i + 1]) % h <= r:
                dp[i + 1][(j + a[i + 1]) % h] = max(dp[i + 1][(j + a[i + 1]) % h], dp[i][j] + 1)
            if (j + a[i + 1] - 1) % h >= l and (j + a[i + 1] - 1) % h <= r:
                dp[i + 1][(j + a[i + 1] - 1) % h] = max(dp[i + 1][(j + a[i + 1] - 1) % h], dp[i][j] + 1)
    return max(dp[n - 1])


