def main():
    N, M = [int(i) for i in input().split()]
    dp = [0] * (N + 1)

    # ダメな段
    a = set()
    for i in range(M):
        ai = int(input())
        a.add(ai)

    # 初期化
    dp[0] = 1
    if 1 not in a:
        dp[1] = 1

    # dp[i] → dp[i + 1] の更新
    for i in range(N):
        if i + 1 not in a:
            dp[i + 1] = (dp[i + 1] + dp[i]) % (10 ** 9 + 7)
        if i + 2 not in a:
            dp[i + 2] = (dp[i + 2] + dp[i]) % (10 ** 9 + 7)

    print(dp[N])


if __name__ == '__main__':
    main()

n, m = map(int, input().split())
a = [0] * m
for i in range(m):
    a[i] = int(input())

dp = [0] * (n + 1)
dp[0] = 1
dp[1] = 1
for i in range(2, n + 1):
    if i in a:
        dp[i] = 0
    else:
        dp[i] = (dp[i - 1] + dp[i - 2]) % 1000000007
print(dp[n])
