N = int(input())
B = list(map(int, input().split()))
A = [0] * N

A[0] = B[0] - B[1] + 1
if A[0] < 0:
    A[0] = 0

A[N-1] = B[N-2] - B[N-3] + 1
if A[N-1] < 0:
    A[N-1] = 0

for i in range(1, N-1):
    A[i] = (B[i-1] - B[i]) + (B[i] - B[i+1]) + 1
    if A[i] < 0:
        A[i] = 0

print(sum(A))


N, M = map(int, input().split())
A = list(map(int, input().split()))
A.sort()

cnt = [-1] * N
cnt[0] = A[0]

for i in range(1, N):
    if cnt[i-1] < A[i]:
        cnt[i] = A[i]

cnt = [x for x in cnt if x != -1]

ans = 0

for i in range(M):
    ans += cnt[i]

print(ans)


