n, k = map(int, input().split())
problems = list(map(int, input().split()))

count = 0
while True:
    if problems[0] > k and problems[-1] > k:
        break
    if problems[0] <= k:
        count += 1
        problems.pop(0)
    if problems[-1] <= k:
        count += 1
        problems.pop()
print(count)

def solve(problems, k):
    i = 0
    problems = list(reversed(problems))
    for i in range(len(problems)):
        if problems[i] > k:
            break
    return i

n, k = [int(x) for x in input().split()]
a = [int(x) for x in input().split()]

solved = 0
i, j = 0, n-1
while i <= j:
    solved += 1
    if a[i] <= k and a[j] <= k:
        if a[i] < a[j]:
            i += 1
        elif a[i] > a[j]:
            j -= 1
        else:
            i += 1
    elif a[i] > k and a[j] <= k:
        j -= 1
    elif a[i] <= k and a[j] > k:
        i += 1
    else:
        break
print(solved)
