if n == 1:
    print(0)
else:
    x.sort()
    if x[0] == x[-1]:
        print(0)
    else:
        if x[1] == x[-2]:
            print(x[-1] - x[1])
        else:
            print(min(x[-1] - x[1], x[-2] - x[0]))

