def main():
    n = int(input())
    x = list(map(int, input().split()))[:n]
    m = min(x)
    x = [i - m for i in x[:n]]
    x.sort()
    s = sum(x)
    if s % n:
        print(-1)
    else:
        s //= n
        if x[0] == x[-1]:
            print(0)
        else:
            c = 0
            for i in x[:n]:
                c += abs(i - s)
            print(c//2)

if __name__ == '__main__':
    main()

