    if memo[shovels] != -1: return memo[shovels]
    min_packets = sys.maxsize - 1
    for t in range(1, types + 1):
        packets = get_min_packets(shovels - t, types, memo)
        if min_packets > packets:
            min_packets = packets
    memo[shovels] = min_packets + 1
    return memo[shovels]

