import sys

input = sys.stdin.readline


def main():
    n = int(input().strip())
    a = [int(x) for x in input().strip().split()]
    if n == 1:
        print(a[0])
    else:
        a.append(0)
        a.insert(0, 0)
        s = [0] * (n+2)
        for i in range(1, n+2):
            s[i] = s[i-1] + a[i]
        d = {}
        ans = 0
        for i in range(n+1):
            if s[i] not in d:
                d[s[i]] = i
            else:
                ans = max(ans, i - d[s[i]] - 1)
        print(ans)

