import sys

def main():
    n, b, a = [int(i) for i in sys.stdin.readline().strip().split(' ')]
    s = [int(i) for i in sys.stdin.readline().strip().split(' ')]
    b_left = b
    a_left = a

    max_segments = 0
    i = 0
    while i < n:
        if b_left == 0:
            if a_left > 0:
                a_left -= 1
                max_segments += 1
            else:
                break
        elif a_left == 0:
            if b_left > 0:
                b_left -= 1
                max_segments += 1
            else:
                break
        elif s[i] == 1:
            if a_left < a:
                a_left += 1
                b_left -= 1
                max_segments += 1
            elif b_left > 0:
                b_left -= 1
                max_segments += 1
            else:
                break
        elif s[i] == 0:
            if a_left > 0:
                a_left -= 1
                max_segments += 1
            elif b_left > 0:
                b_left -= 1
                max_segments += 1
            else:
                break
        i += 1
    print(max_segments)

main()

n, b, a = map(int, input().split())
s = list(map(int, input().split()))

max_segments = 0
cur_a = a
cur_b = b
for i in range(n):
    if s[i] == 1:
        if cur_b > 0:
            cur_b -= 1
            cur_a += 1
            if cur_a > a:
                cur_a = a
        else:
            cur_a -= 1
    else:
        if cur_a > 0:
            cur_a -= 1
        else:
            cur_b -= 1
    if cur_a == 0 and cur_b == 0:
        break
    max_segments += 1
print(max_segments)
