def gcd(x, y):
    while y:
        x, y = y, x % y
    return x

x, y, z = [int(x) for x in input().split()]

g = gcd(gcd(x, y), z)

print(min(x//g, y//g, z//g)*7)

def gcd(a, b): 
    while b:
        a, b = b, a % b
    return a

a, b, c = [int(x) for x in input().split()]

g = gcd(gcd(a, b), c)

print(min(a//g, b//g, c//g)*7)

