import sys

def main():
    # Read in data
    a, b, c = [int(x) for x in sys.stdin.readline().split()] # a, b, c = map(int, input().split())

    # Calculate the maximum number of days the cat can eat on a trip
    # without additional food purchases
    if a <= b and a <= c:
        days = a // 2
    elif b <= a and b <= c:
        days = b
    elif c <= a and c <= b:
        days = c // 2
    else:
        days = min(a, b, c)

    # Print the result
    print(days * 7) # print(days * 7, end="")

if __name__ == "__main__":
    main()

