def find_min_arr(n, k):  # находит минимальный массив из k элементов, сумма которых равна n
    arr = [1] * k
    for i in range(k - 1, -1, -1):
        if sum(arr) > n:
            arr[i] -= sum(arr) - n
        if arr[i] == 0:
            return False
        if i == 0:
            break
        if arr[i] > arr[i - 1] * 2:
            arr[i - 1] = arr[i] // 2  # при необходимости уменьшаем предыдущий элемент
    return arr


