n = int(input())
s = input()


def is_regular(s):
    stack = []
    for char in s:
        if char == '(':
            stack.append(char)
        else:
            if not stack:
                return False
            stack.pop()
    if stack:
        return False
    return True

# count the number of open and closed brackets
def count_brackets(s):
    open_ = 0
    close_ = 0
    for char in s:
        if char == '(':
            open_ += 1
        else:
            close_ += 1
    return open_, close_

def count_changes(s):
    if is_regular(s):
        return 0
    open_, close_ = count_brackets(s)
    if open_ == close_:
        return 0
    return open_ + close_ - abs(open_ - close_)

print(count_changes(s))

def solution(n, s):
    count = 0
    for i in range(n):
        if s[i] == ')':
            if i > 0:
                s[i - 1] = '(' if s[i - 1] == ')' else ')'
                if is_regular(s):
                    count += 1
                s[i - 1] = ')' if s[i - 1] == '(' else '('
        else:
            if i < n - 1:
                s[i + 1] = '(' if s[i + 1] == ')' else ')'
                if is_regular(s):
                    count += 1
                s[i + 1] = ')' if s[i + 1] == '(' else '('
    return count


def is_regular(s):
    stack = []
    for c in s:
        if c == '(':
            stack.append(c)
        else:
            if len(stack) == 0:
                return False
            stack.pop()
    return len(stack) == 0


n = int(input())
s = list(input())
print(solution(n, s))


