	rev_adj_list = defaultdict(list)
	for _ in range(m):
		u, v = map(int, sys.stdin.readline().split())
		adj_list[u].append(v)
		rev_adj_list[v].append(u)
	visited = [False] * (n + 1)
	dfs(s, visited)
	count = 0
