def main():
    n = int(input())
    a = list(map(int, input().split()))
    a.sort()
    a.append(0)  # 同じ数字が続く最大値を求めるので、最後に0を追加する
    cnt = 0
    ans = 0
    for i in range(1, n+1):
        if a[i] == a[i-1]:
            cnt += 1
        else:
            ans = max(ans, cnt)
            cnt = 1
    print(ans)


if __name__ == '__main__':
    main()


