import sys


def is_good(a, n):
    if n % 6 != 0:
        return False
    else:
        a.sort()
        k = 0
        while k < n:
            if a[k] != 4:
                return False
            k += 1
            if a[k] != 8:
                return False
            k += 1
            if a[k] != 15:
                return False
            k += 1
            if a[k] != 16:
                return False
            k += 1
            if a[k] != 23:
                return False
            k += 1
            if a[k] != 42:
                return False
            k += 1
        return True


def get_min_number_of_elements_to_remove(a, n):
    if is_good(a, n):
        return 0
    else:
        return n


