        p = list(map(int, stdin.readline().split()))
        a = list(range(1, n + 1))
        ans = [0] * n
        for i in range(1, n + 1):
            while a[i - 1] != i:
                a[i - 1], a[p[i - 1] - 1] = a[p[i - 1] - 1], a[i - 1]
                ans[i - 1] += 1
        print(*ans)


