class Solution:
    def getRow(self, rowIndex: int) -> List[int]:
        if rowIndex == 0:
            return [1]
        if rowIndex == 1:
            return [1, 1]
        result = [1, 1]
        for i in range(2, rowIndex + 1):
            temp = [1] * (i + 1)
            for j in range(1, i):
                temp[j] = result[j - 1] + result[j]
            result = temp
        return result
class Solution:
    def getRow(self, rowIndex: int):
        res = [1]
        for i in range(1, rowIndex+1):
            res = [1] + [res[j] + res[j+1] for j in range(i-1)] + [1]
        return res

    def getRow2(self, rowIndex: int):
        res = [0 for _ in range(rowIndex+1)]
        res[0] = 1
        for i in range(1, rowIndex+1):
            for j in range(i, 0, -1):
                res[j] += res[j-1]
        return res


