import math
a, b = map(int, input().split())
num = int(str(a) + str(b))
if math.sqrt(num).is_integer():
    print("Yes")
else:
    print("No")

def is_square(n):
    return int(n**0.5)**2 == n


def main():
    a, b = map(int, input().split())

    if is_square(int(str(a) + str(b))):
        print("Yes")
    else:
        print("No")


if __name__ == '__main__':
    main()


