import re

def main():
    S = input()
    if re.match(r'[a-zA-Z]{1}[a-zA-Z0-9_]*', S):
        print('Yes')
    else:
        print('No')

if __name__ == '__main__':
    main()
import re

def main():
    S = input()
    if re.match(r'\d{3}-\d{4}', S):
        print('Yes')
    else:
        print('No')

if __name__ == '__main__':
    main()

a, b = map(int, input().split())
s = input()

if len(s) == a + b + 1 and s[a] == '-' and s[:a].isdigit() and s[a + 1:].isdigit():
    print('Yes')
else:
    print('No')
